mapper.insert:

  SELECT LAST_INSERT_ID()

insert into course (course_name, course_code, course_creat_time
  )
values (#{courseName,jdbcType=VARCHAR}, #{courseCode,jdbcType=VARCHAR}, #{courseCreatTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update course
set course_name = #{courseName,jdbcType=VARCHAR},
  course_code = #{courseCode,jdbcType=VARCHAR},
  course_creat_time = #{courseCreatTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, course_name, course_code, course_creat_time
from course
where id = #{id,jdbcType=BIGINT}

select id, course_name, course_code, course_creat_time
from course where  1=1

  and  course_name=#{courseName}
